import React, {useState} from 'react'

function ClassLabelSubmit() {
    const [classLabel, setClassLabel] = useState("")

  return (
    <div>
        <form>
            <label>Class Name: </label>
            <div className='flex'>
                <input class="placeholder:italic placeholder:text-slate-400 block bg-white w-1/4 border border-slate-300 rounded-md py-2 pl-9 pr-3 shadow-sm focus:outline-none focus:border-sky-500 focus:ring-sky-500 focus:ring-1 sm:text-sm" 
                placeholder="Class Name..." 
                type="text" 
                name="search"
                onChange={
                    (e) => {
                        setClassLabel(classLabel => [...classLabel, e]);
                        console.log(classLabel);
                    }
                }/>
                <button class="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded-full mx-4">
                    Submit Class
                </button>
            </div>
        </form>
    </div>
  )
}

export default ClassLabelSubmit
