import React from 'react'
import Webcam from 'react-webcam'

const videoContraints = {
    width: 400,
    height: 400,
    facingMode: "user",
}

var imageSrc = ""
var clicked = false

function ImageCap() {
  return (
    <div>
        <Webcam
        audio={false}
        videoConstraints={videoContraints}
        screenshotFormat="image/jpeg"
        height={400}
        width={400}>
            {({ getScreenshot }) => (
                <button 
                onClick={() => {
                    const imageSrc = getScreenshot({width: 1920, height: 1080})
                    console.log(imageSrc)
                }}
            >
                Capture Image
            </button>
            )}
        </Webcam>
        {
            clicked ? <img src={`data:image/jpeg;base64,${imageSrc}`} /> : <h1>None Selected</h1>
        }
    </div>
  )
}

export default ImageCap
