import React from 'react'
import Webcam from 'react-webcam'

const videoContraints = {
    width: 400,
    height: 400,
    facingMode: "user",
}

function ImageCap() {
  return (
    <div>
        <Webcam
        audio={false}
        videoConstraints={videoContraints}
        screenshotFormat="image/jpeg"
        height={400}
        width={400}>
            </Webcam>
        <button 
        onClick={() => {
            const imageSrc = getScreenshot()
        }}
        >
            Capture Image
        </button>
    </div>
  )
}

export default ImageCap
